import React, { useState, useEffect } from 'react';
import { NavLink, useLocation } from 'react-router-dom';
import '../css/estilos.css';
import { SuAPI } from '../components/SuAPI';
import ReactLogo from '../img/react.png';
import { FaUser, FaSignInAlt } from 'react-icons/fa'; // Import icons

export const MiNavBar = ({ products }) => {
  const [darkMode, setDarkMode] = useState(true);

  const toggleTheme = () => {
    setDarkMode(!darkMode);
    document.body.classList.toggle('light-theme', darkMode);
  };

  return (
    <nav className="navbar">
      <img className="react-logo" src={ReactLogo} alt="React-Logo" />
      <h1>Proyecto React Pablo</h1>
      <ul className="nav-list">
        <li><NavLink to="/api" activeClassName="active">API</NavLink></li>
        <li>
          <SuAPI></SuAPI>
        </li>
        <li><NavLink to="/contador" activeClassName="active">Contador</NavLink></li>
        <li><NavLink to="/gestor" activeClassName="active">Gestor</NavLink></li>
        <li><NavLink to="/interactive" activeClassName="active">Componentes</NavLink></li>
        <li><NavLink to="/informes" activeClassName="active">Informes</NavLink></li>
        <div className="user-links">
          <NavLink to="/login" activeClassName="auth-active"><FaSignInAlt /></NavLink>
          <NavLink to="/user" activeClassName="auth-active"><FaUser /></NavLink>
        </div>
      </ul>
      <label className="switch">
        <input type="checkbox" onChange={toggleTheme} />
        <span className="slider"></span>
        <img src="data:image/jpeg;base64,"
          alt="Off"
          className="off" />
        <img src="data:image/jpeg;base64,"
          alt="On"
          className="on" />
      </label>
    </nav>
  );
};
